import React, { Component } from "react";
import axios from "axios";
import { saveAs } from "file-saver";
import Nav from "./Components/Nav";
import StartForm from "./Components/Form";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: null,
      attachement: new Blob(),
      item: {
        id: "",
        email: "",
        personCompleting: "",
        accountName: "",
        contactName: "",
        address: "",
        phoneNumber: "",
        emergencyContact: "",
        emergencyContactNumber: "",
        customerEmail: "",
        billingAddress: "",
        walkthroughDate: new Date(),
        startDate: new Date(),
        franchisee: "",
        franchiseeEmail: "",
        daysCleaned: [],
        timeCleaned: "",
        monthlyBillingAmount: "",
        initialClean: "",
        initialCleanAmount: "",
        buildingEntry: "",
        communicationLogBook: "",
        lightSwitchLocations: "",
        mainPowerShutoff: "",
        alarmSystem: "",
        alarmCode: "",
        storageAreas: "",
        storageKey: "",
        waterLocation: "",
        waterTemp: "",
        suppliesFurnish: "",
        includedSupplies: "",
        trashDisposalLocation: "",
        keyEntry: "",
        restroomSupply: "",
        storageCloset: "",
        commentsText: ""
      }
    };

    this.handleInputChange = this.handleInputChange.bind(this);
    // this.handleCheckmarkChange = this.handleCheckmarkChange.bind(this);
    this.onWalkDateChange = this.onWalkDateChange.bind(this);
    this.onStartDateChange = this.onStartDateChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidMount() {
    // Call our fetch function below once the component mounts
    this.callBackendAPI()
      .then(res => this.setState({ data: res.express }))
      .catch(err => console.log(err));
  }
  // Fetches our GET route from the Express server. (Note the route we are fetching matches the GET route from server.js
  callBackendAPI = async () => {
    const response = await fetch("/express_backend");
    const body = await response.json();

    if (response.status !== 200) {
      throw Error(body.message);
    }
    return body;
  };

  handleInputChange = e => {
    e.preventDefault();
    const target = e.target;
    const value = target.type === "checkbox" ? target.checked : target.value;
    const name = target.name;
    console.log(name, value);

    this.setState(prevState => ({
      item: { ...prevState.item, [name]: value }
    }));
  };

  onWalkDateChange = date => {
    this.setState(prevState => ({
      item: { ...prevState.item, walkthroughDate: date }
    }));
    console.log(date);
  };

  onStartDateChange = date => {
    this.setState(prevState => ({
      item: { ...prevState.item, startDate: date }
    }));
    console.log(date);
  };

  handleSubmit = e => {
    e.preventDefault();
    this.createAndDownloadPdf();
    const obj = this.state.item;
    this.createAndSendEmail(obj);
  };

  createAndDownloadPdf = () => {
    axios
      .post("/create-pdf", this.state.item)
      .then(() => axios.get("/fetch-pdf", { responseType: "blob" }))
      .then(res => {
        const pdfBlob = new Blob([res.data], { type: "application/pdf" });
        this.setState({})
        saveAs(pdfBlob, this.state.item.accountName + "StratusStartForm.pdf");
      });
  };

  createAndSendEmail = () => {
    const from = "brenden.d.coleman@gmail.com";
    const toField = [
      from,
      this.state.item.email,
      this.state.item.franchiseeEmail
    ];
    const account = this.state.item.accountName;
    const emailData = {
      from: from,
      to: toField,
      subject: "New Start Form submission for " + account,
      body: "Testing and testing",
      html: "<strong>and easy to do anywhere, even with Node.js</strong>"
    };
    axios.post("/send-email", emailData).then(res => {
      console.log(res);
      console.log(res.data);
    });
  };

  render() {
    return (
      <React.Fragment>
        <Nav />
        <br />
        <h1>Stratus New Account Start Form</h1>
        <br />
        <StartForm
          item={this.state.item}
          handleInputChange={this.handleInputChange}
          onWalkDateChange={this.onWalkDateChange}
          onStartDateChange={this.onStartDateChange}
          handleSubmit={this.handleSubmit}
        />
      </React.Fragment>
    );
  }
}

export default App;
