import React, { Component } from "react";
import axios from "axios";
import { saveAs } from "file-saver";
import Nav from "./Components/Nav";
import StartForm from "./Components/Form";
import { Container } from "react-bootstrap";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: null,
      item: {
        id: "",
        email: "",
        personCompleting: "",
        accountName: "",
        contactName: "",
        address: "",
        customerEmail: "",
        walkthroughDate: new Date(),
        startDate: new Date(),
        franchisee: "",
        franchiseeEmail: "",
      }
    };

    this.handleInputChange = this.handleInputChange.bind(this);
    this.onWalkDateChange = this.onWalkDateChange.bind(this);
    this.onStartDateChange = this.onStartDateChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.createAndSendEmail = this.createAndSendEmail.bind(this);
  }

  componentDidMount() {
    // Call our fetch function below once the component mounts
    this.callBackendAPI()
      .then(res => this.setState({ data: res.express }))
      .catch(err => console.log(err));
  }

  // Fetches our GET route from the Express server. (Note the route we are fetching matches the GET route from server.js
  callBackendAPI = async () => {
    const response = await fetch("/express_backend");
    const body = await response.json();

    if (response.status !== 200) {
      throw Error(body.message);
    }
    return body;
  };

  handleInputChange = e => {
    e.preventDefault();
    const target = e.target;
    const value = target.value;
    const name = target.name;
    console.log(name, value);

    this.setState(prevState => ({
      item: { ...prevState.item, [name]: value }
    }));
  };

  onWalkDateChange = date => {
    this.setState(prevState => ({
      item: { ...prevState.item, walkthroughDate: date }
    }));
    console.log(date);
  };

  onStartDateChange = date => {
    this.setState(prevState => ({
      item: { ...prevState.item, startDate: date }
    }));
  };

  handleSubmit = e => {
    e.preventDefault();
    this.createPDF();
    this.createAndSendEmail();
  };

  createPDF = () => {
    axios
      .post("/create-pdf", this.state.item)
      .then(() => axios.get("/fetch-pdf", { responseType: "blob" }))
      .then(res => {
        const pdfBlob = new Blob([res.data], { type: "application/pdf" });
        saveAs(pdfBlob, this.state.item.accountName + "StratusStartForm.pdf");
      });
  };

  createAndSendEmail = () => {
    console.log("creating email");
    const content = { ...this.state.item };
    //INSERT THE EMAIL YOU WANT TO SEND FROM OR (RECOMMENDED) ADD IT TO A .ENV FILE
    const from = "brenden.d.coleman@gmail.com";
    const toField = [from, content.email, content.franchiseeEmail];
    const acc = content.accountName;
    const account = acc.split(" ").join("");
    const emailData = {
      from: from,
      to: toField,
      subject: "New Start Form submission for " + account,
      account: account,
      content: content
    };

    axios
      .post("/send-email", emailData)
      .then(res => {
        console.log(res);
        console.log(res.data);
      })
      .catch(error => {
        console.log(error.message);
      });
  };

  render() {
    return (
      <React.Fragment>
        <Nav />
        <br />
        <Container>
          <h1>Company_Name New Account Start Form</h1>
          <br />
          <StartForm
            item={this.state.item}
            handleInputChange={this.handleInputChange}
            onWalkDateChange={this.onWalkDateChange}
            onStartDateChange={this.onStartDateChange}
            handleSubmit={this.handleSubmit}
          />
        </Container>
      </React.Fragment>
    );
  }
}

export default App;
